    <script>

        $(function () {
            const Toast = Swal.mixin({
                toast: true,
                position: 'top-end',
                showConfirmButton: false,
                timer: 3000
            });

            //ABOUTCOMPONENT DELETE
            $(document).on(""click"", "".AboutComponentDelete"", function () {
                var id = $(this).attr(""data-id"");
                $(""#AboutComponentID"").val(id);
            });

            $(""#AboutComponentDelete"").click(function () {
                var id = $(""#AboutComponentID"").val();
                $.ajax({
                    type: ""POST"",
